import React, { Component } from "react";
import Layout from "../components/Layout";
import axios from "axios";
import Link from "next/link";

class Index extends Component{

    constructor(props)
    {
        super(props);
        this.state = {blogs : ""};
    }
    
    componentDidMount()
    {
    this.setState({blogs : [
        {   id: 1,
            thumbnail: "https://assets.brandinside.asia/uploads/2017/07/one-piece-cover.png",
            subject: "ทดสอบ หัวข้อที่ 1",
            intro: "หัวข้อย่อยที่ 1111111",
            detail: "ข้อมูลเนื้อหาทั้งหมดที่ควรมีของ 1",
            categories: ["Game"],
            created_by: {"user" : { "firstname": "KeiP."}},
            created_date: "2019-01-11",
            updated_by: {"user" : { "firstname": "KeiP."}},
            updated_date: ""
        }    
    ]})
    }


   /* static async getInitialProps()
    {
        const res = await axios.get("http://localhost:3001/blogs/", {
            headers: {
              'Access-Control-Allow-Origin': '*',
            },
        });
        return { blogs : res.data }
    }*/

    renderBlogs = blogs => {
        return(
            blogs.map(blog => {
                return(
                    <div key={blog.id} className="col-6">
                        <img src={blog.thumbnail} className="img-fluid" />
                        <h4 className="mt-3">
                            <Link href={'/blog/detail?id='+ blog.id}>
                                <a>
                                    {blog.subject}
                                </a>
                            </Link>
                        </h4>
                        <hr/>
                        <p className="text-justify">{blog.intro}</p>
                    </div>
                )
            })
        )
    }

    render(){
        console.log(this.props.blogs);
        return(
            <Layout>
                <div className="container">
                    <div className="row">
                        {this.renderBlogs(this.props.blogs)}
                    </div>
                </div>               
            </Layout>               
        )
    }
}

export default Index;
//export default () => <div>Welcome to next.js</div>


