import React, { Component } from "react";
import Layout from "../../components/Layout";
import Head from "next/head";
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";
class Theresume extends Component {

    render() {
        return (
            <Layout title = "The Resume Game | AOWSAY">                
                <BrowserView>
                <div className="container text-center">
                    <h1>The Resume Game</h1>
                    <div className="row d-flex justify-content-center">                        
                        <div className="shadow-lg p-3 mb-5 bg-dark rounded">
                            <iframe frameborder="0" src="https://itch.io/embed-upload/1857824?color=333333" allow="fullscreen" width="940" height="510">
                                <a href="https://msomsap.itch.io/the-resume-game">Play The  Resume Game on itch.io</a>
                            </iframe>
                        </div>
                    </div>
                    <br />
                </div> 
                </BrowserView>                
                <MobileView>
                <Head>
                    <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1" />
                    <script src="https://aframe.io/releases/0.7.1/aframe.min.js"></script>
                </Head>
                    <style jsx>{`
                        iframe 
                        {
                         display: block; 
                         width: 100%; 
                         border: none; 
                         overflow-y: auto; 
                         overflow-x: hidden;
                        }
                    `}</style>
                    <iframe frameborder="0" 
                    src="https://itch.io/embed-upload/1857824?color=333333" 
                    allow="fullscreen" 
                    frameborder="0" 
                    marginheight="0" 
                    marginwidth="0" 
                    width="100%" 
                    height="100%"
                    >
                        <a href="https://msomsap.itch.io/the-resume-game">Play The  Resume Game on itch.io</a>
                    </iframe>
                </MobileView>              
            </Layout>
        )
    }
}

export default Theresume;
