import React from 'react';
import { Slide } from 'react-slideshow-image';
 
const slideImages = [
  'images/slide_2.jpg',
  'images/slide_3.jpg',
  'images/slide_4.jpg'
];
 
const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
  onChange: (oldIndex, newIndex) => {
    console.log(`slide transition from ${oldIndex} to ${newIndex}`);
  }
}
 
const Slideshow = () => {
    return (   
    <div className="slide-container">
         <style jsx>{`
                .slide-container {
                    width: 70%;
                    margin: auto; }
                
                h3 {
                    text-align: center; }
                
                .each-slide > div {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    background-size: cover;
                    height: 300px;
                }
                
                .each-slide span {
                    padding: 20px;
                    font-size: 20px;
                    background: #efefef;
                    text-align: center;
                }
                
                .each-fade {
                    display: flex;
                }
                
                .each-fade .image-container {
                    width: 75%;
                    overflow: hidden;
                }
                
                .each-fade .image-container img {
                    width: 100%;
                }
                
                .each-fade h2 {
                    width: 25%;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    margin: 0;
                    background: #adceed;
                }                       
    `}</style>
        <Slide {...properties}>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
              <span>Slide 1</span>
            </div>
          </div>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
              <span>Slide 2</span>
            </div>
          </div>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
              <span>Slide 3</span>
            </div>
          </div>
        </Slide>
      </div>
    )
}

export default Test;
