import React, { Component } from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import GameLayout from "../../components/GameLayout";
import {
    BrowserView,
    MobileView,
  } from "react-device-detect";
class Theresume extends Component {

    render() {
        return (   
            <GameLayout title = "The Resume Game | AOWSAY">   

                <BrowserView>
                <Header />
                <div className="container text-center">
                    <h1>The Resume Game</h1>
                    <div className="row d-flex justify-content-center">                        
                        <div className="shadow-lg p-3 mb-5 bg-dark rounded">
                            <iframe frameborder="0" src="https://itch.io/embed-upload/1857824?color=333333" allow="fullscreen" width="940" height="510">
                                <a href="https://msomsap.itch.io/the-resume-game">Play The  Resume Game on itch.io</a>
                            </iframe>
                        </div>
                    </div>
                    <br />
                </div>
                <Footer />
                </BrowserView>  
                <meta name="viewport" content="minimal-ui, width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no"></meta>
                    <MobileView>
                        <style jsx>{`
                            iframe 
                            {
                                margin: 0;
                                position: absolute;
                                top: 20px;
                                left: 0;
                                width: 100%;
                                height: 90%;
                                border: none; 
                                overflow-x: hidden; 
                                overflow-y: hidden;                         
                            }
                        `}</style>                        
                        <iframe 
                        src="https://itch.io/embed-upload/1857824?color=333333" 
                        allowfullscreen="allowfullscreen" 
                        frameBorder="0" 
                        marginheight="0" 
                        marginwidth="0"
                        >
                            <a href="https://msomsap.itch.io/the-resume-game">Play The  Resume Game on itch.io</a>
                        </iframe>
                </MobileView>      
            </GameLayout> 
        )
    }
}

export default Theresume;
