import React from "react";
import Slider from "react-slick";
import Head from "next/head";
class SlideShow extends React.Component {
  render() {
    var settings = {
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
    };
    return (
        <div>
        <Head>
            <link rel="stylesheet" type="text/css" charset="UTF-8" href="/static/css/slick1.css" />
            <link rel="stylesheet" type="text/css" charset="UTF-8" href="/static/css/slick2.css" />
        </Head>
        <div className="container-fluid">
            <Slider {...settings}>
                
                <div className="row">
                    <div className="d-flex justify-content-center">
                        <img style={{width:700}} src="/static/images/test/test1.png" className="img-fluid" />
                    </div>
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test2.png" className="img-fluid" />
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test3.png" className="img-fluid" />
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test4.png" className="img-fluid" />
                </div>
            </Slider>
        </div>
      </div>
    );
  }
}

export default SlideShow;
