import React, { Component } from "react";
import Layout from "../../components/Layout";
import axios from "axios";

class Detail extends Component {

    static async getInitialProps ({ query}) {
        const res = await axios.get('https://us-central1-aowsay.cloudfunctions.net/aowsay' + query.id);
        return { blog : res.data}
    }
    render(){
        const {blog} = this.props;
        return(
            <Layout>
                <div className="container">
                    <h1 className="mt-4 mb-4 title">{blog.subject}</h1>
                    <div className="row">
                        <div className="col-6">
                            <img className="img-fluid" src={blog.thumbnail} />
                        </div>
                        <div className="col-6">
                            <div className="text-justify detail">
                                {blog.detail}
                            </div>
                            <hr/>
                            <h5 className="text-success">
                                เขียนโดย {blog.created_by.user.firstname}
                            </h5>
                            <h5 className="text-muted text-right">
                                แก้ไขเมื่อ {blog.updated_date}
                            </h5>
                        </div>
                    </div>
                </div>
            </Layout>
        )
    }

    render()
    {
        return(<Layout>

        </Layout>)
    }
}

export default Detail;
