import React from "react";
import Slider from "react-slick";
import Head from "next/head";
class SlideShow extends React.Component {
  render() {
    var settings = {
        centerMode: true,
      centerPadding: '60px',
      slidesToShow: 3,
      prevArrow: $('.prev'),
      nextArrow: $('.next'),
    };
    return (
        <div>
        <Head>
            <link rel="stylesheet" type="text/css" charset="UTF-8" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css" />
            <link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css" />
        </Head>
        <div className="container-fluid">
            <style jsx>{`
                img{
                    box-shadow: 10px 10px 5px grey;
                }
                .slick-prev:before {
                    content: "<";
                    color: red;
                    font-size: 30px;
                  }
                  
                  .slick-next:before {
                    content: ">";
                    color: red;
                    font-size: 30px;
                  }                
                                   
             `}</style>
            <Slider {...settings}>
                
                <div className="row">
                    <div className="d-flex justify-content-center">
                        <img style={{width:700}} src="/static/images/test/test1.png" className="img-fluid" />
                    </div>
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test2.png" className="img-fluid" />
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test3.png" className="img-fluid" />
                </div>
                <div>
                        <img style={{width:700}} src="/static/images/test/test4.png" className="img-fluid" />
                </div>
            </Slider>
        </div>
      </div>
    );
  }
}

export default SlideShow;
