import React, { Component } from "react";
import Layout from "../components/Layout";
import axios from "axios";
import Link from "next/link";

class Index extends Component{

    static async getInitialProps()
    {
        const res = await axios.get("https://us-central1-aowsay.cloudfunctions.net/aowsay");
        return { blogs : res.data }
    }

    renderBlogs = blogs => {
        return(
            blogs.map(blog => {
                return(
                    <div key={blog.id} className="col-6">
                        <img src={blog.thumbnail} className="img-fluid" />
                        <h4 className="mt-3">
                            <Link href={'/blog/detail?id='+blog.id}>
                                {blog.subject}
                            </Link>
                        </h4>
                        <hr/>
                        <p className="text-justify">{blog.intro}</p>
                    </div>
                )
            })
        )
    }

    render(){
        console.log(this.props.blogs);
        return(
            <Layout>
                <div className="container">
                    ธฎฆธธธธ
                </div>               
            </Layout>               
        )
    }
}

export default Index;
//export default () => <div>Welcome to next.js</div>
